class ContainerSlideLeft extends React.Component {

    render() {
        return (
            <div className="shadow reveal main slide-right grow pt-4 pr-4 pl-4 pb-5 mb-5">
                <h3 className="reveal reveal-2 slide-top mb-4">{this.props.title}</h3>
                    <div className="reveal reveal-3 slide-right body">{this.props.body}</div>
                    <style jsx>{`
                    .reveal.main {
                        color: white;
                        height: auto;
                        padding: 10px 20px;
                        margin: 40px 0;
                        background: linear-gradient(5deg,#004161fc ,#0f4769e8);
                    }
                    .reveal.body {
                        line-height: 1.8rem;
                        font-weight: 100;
                        font-size: 1.15rem;
                    }
                    .expand-left {
                        animation-name: expand-left;
                        animation-duration: 1s;
                        animation-timing-function: ease-out;
                    }
                    .reveal .reveal {
                        opacity: 0;
                    }
                    .reveal.slide-top {
                        animation-name: slide-top;
                        animation-duration: 1s;
                        animation-fill-mode: forwards;
                    }
                    .reveal.slide-right {
                        animation-name: slide-right;
                        animation-duration: 1s;
                        animation-fill-mode: forwards;
                    }
                    .reveal.reveal-2 {
                        animation-delay: .8s;
                    }
                    .reveal.reveal-3 {
                        animation-delay: 1.4s;
                    }
                    @keyframes slide-top {
                        0% {
                            transform: translate(0, -40px);
                            opacity: 0;
                        }
                        100% {
                            transform: translate(0, 0);
                            opacity: 1;
                        }
                    }
                    @keyframes slide-bottom {
                        0% {
                            transform: translate(0, 40px);
                            opacity: 0;
                        }
                        100% {
                            transform: translate(0, 0);
                            opacity: 1;
                        }
                    }
                    @keyframes slide-right {
                        0% {
                            transform: translate(40px, 0);
                            opacity: 0;
                        }
                        100% {
                            transform: translate(0, 0);
                            opacity: 1;
                        }
                    }
                    @keyframes expand-left {
                        0% {
                            opacity: 0; 
                            width: 0px;
                        }
                        100% {opacity: 1; 
                            width: 100px;
                            height: 66px;
                        }
                    }
                `}</style>
            </div>
        )
    }
}

export default ContainerSlideLeft;
