class ContainerFadeIn extends React.Component {

    componentDidMount() {
        console.log(this);
        let options = {
            threshold: 1
        }
        let target = document.querySelector(`#${this.props.id}`);

        function callback(entries) {
            entries.forEach((entry, index) => {
                if (entry.isIntersecting) {
                    entry.target.classList.add("reveal");
                    Array.from(entry.target.children).forEach(cur => cur.classList.add('reveal'));
                    observer.unobserve(entry.target);
                    observer.disconnect();
                }
            });
        }

        let observer = new IntersectionObserver(callback, options);
        observer.observe(target);
    }

    render() {
        return (
            <div id={this.props.id} className="shadow main fade-in pt-4 pr-4 pl-4 pb-5 mb-5">
                <h3 className="reveal-2 slide-top mb-4">{this.props.title}</h3>
                    <div className="reveal-3 slide-right body">{this.props.body}</div>
                    <style jsx>{`
                    .reveal.main {
                        color: white;
                        height: auto;
                        padding: 10px 20px;
                        margin: 40px 0;
                        background: linear-gradient(5deg,#004161fc ,#0f4769e8);
                    }
                    .reveal.body {
                        line-height: 1.8rem;
                        font-weight: 100;
                        font-size: 1.15rem;
                    }
                    .expand-left {
                        animation-name: expand-left;
                        animation-duration: 1s;
                        animation-timing-function: ease-out;
                    }
                    .reveal, div, h3 {
                        opacity: 0;
                    }
                    .reveal.fade-in {
                        animation-name: fade-in;
                        animation-duration: 1s;
                        animation-fill-mode: forwards;
                    }
                    .reveal.slide-top {
                        animation-name: slide-top;
                        animation-duration: 1s;
                        animation-fill-mode: forwards;
                    }
                    .reveal.slide-right {
                        animation-name: slide-right;
                        animation-duration: 1s;
                        animation-fill-mode: forwards;
                    }
                    .reveal.reveal-2 {
                        animation-delay: .8s;
                    }
                    .reveal.reveal-3 {
                        animation-delay: 1.4s;
                    }
                    @keyframes slide-top {
                        0% {
                            transform: translate(0, -40px);
                            opacity: 0;
                        }
                        100% {
                            transform: translate(0, 0);
                            opacity: 1;
                        }
                    }
                    @keyframes slide-bottom {
                        0% {
                            transform: translate(0, 40px);
                            opacity: 0;
                        }
                        100% {
                            transform: translate(0, 0);
                            opacity: 1;
                        }
                    }
                    @keyframes slide-right {
                        0% {
                            transform: translate(40px, 0);
                            opacity: 0;
                        }
                        100% {
                            transform: translate(0, 0);
                            opacity: 1;
                        }
                    }
                    @keyframes expand-left {
                        0% {
                            opacity: 0; 
                            width: 0px;
                        }
                        100% {opacity: 1; 
                            width: 100px;
                            height: 66px;
                        }
                    }
                    @keyframes fade-in {
                        0% {
                            opacity: 0; 
                            transform: scale(.92, .92);
                        }
                        100% {
                            opacity: 1; 
                            transform: scale(1, 1);
                        }
                    }
                `}</style>
            </div>
        )
    }
}

export default ContainerFadeIn;
